function closeWindows(){modalCart.classList.contains("modal-show")&&modalCart.classList.remove("modal-show")}var modalCart=document.querySelector(".modal-cart"),buttonBuy=document.querySelectorAll(".buy"),modalClose=document.querySelectorAll(".modal-close"),btnContinue=document.querySelector(".continue");window.addEventListener("keydown",function(e){27==e.keyCode&&closeWindows()});for(var i=0;i<modalClose.length;i++)modalClose[i].addEventListener("click",function(e){e.preventDefault(),closeWindows()});for(var i=0;i<buttonBuy.length;i++)buttonBuy[i].addEventListener("click",function(e){e.preventDefault(),closeWindows(),modalCart.classList.add("modal-show")});btnContinue.addEventListener("click",function(e){e.preventDefault(),closeWindows()});